import React, { Component } from 'react';
import './App.css';

import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      { name: 'Govind', age: 23 },
      { name: 'Yash', age: 24 },
      { name: 'Panda', age: 26}
    ]
  }

  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        { name: 'Govind Kumar Meena', age: 23 },
        { name: newName, age: 24 },
        { name: 'Panad', age: 27 }
      ]
    })
  }
  render() {
    return (
      <div className="App">
        <p>Hello, I'm Govind Kumar Meena</p>
        <button onClick={this.switchNameHandler.bind(this, 'YashSony')} >Switch</button>
        <Person 
        name={this.state.persons[0].name}
        age={this.state.persons[0].age}></Person>
        <Person 
        name={this.state.persons[1].name} 
        age={this.state.persons[1].age}>My Hobbies: playing fotball</Person>
        <Person 
        name={this.state.persons[2].name} 
        age={this.state.persons[2].age}
        click={this.switchNameHandler.bind(this, 'GKM')}></Person>
      </div>
    );
  }
}

export default App;
