import React, { Component } from 'react';
import './App.css';

import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      { name: 'Govind', age: 23 },
      { name: 'Yash', age: 24 },
      { name: 'Panda', age: 26}
    ],
    showPerson: false
  }
  togglePersonHandler = () => {

  }

  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        { name: 'Govind Kumar Meena', age: 23 },
        { name: newName, age: 24 },
        { name: 'Panad', age: 27 }
      ]
    })
  }

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        { name: 'Govind Kumar Meena', age: 23 },
        { name: 'value', age: 24 },
        { name: event.target.value, age: 27 }
      ]
    })
  }
  render() {
    return (
      <div className="App">
        <h1>Hi, I'm React App</h1>
        <p>Hello, I'm Govind Kumar Meena</p>
        <button onClick={() => this.switchNameHandler('YashSony')} >Switch</button>
        { !this.state.showPerson ?
        <div >
        <Person 
        name={this.state.persons[0].name}
        age={this.state.persons[0].age}></Person>
        <Person 
        name={this.state.persons[1].name} 
        age={this.state.persons[1].age}>My Hobbies: playing fotball</Person>
        <Person 
        name={this.state.persons[2].name} 
        age={this.state.persons[2].age}
        click={this.switchNameHandler.bind(this, 'GKM')}
        changed={this.nameChangeHandler} ></Person>
        </div> : null
        }
      </div>
    );
  }
}

export default App;
